import React from 'react'
import { useState} from "react";
import { BrowserRouter, Routes, Route } from 'react-router-dom'
import Layout from './components/layout/Layout'
import Home from './components/Home'
import ContactUs from './components/ContactUs';
import AboutUs from './components/AboutUs';
import User from './components/User';


function App ()  {
  // const [selectedUser, setSelectedUser] = useState('');
  const [userRepo, setUserRepo] = useState([])
  const [displayUserInfo, setDisplayUserInfo] = useState(false);

  function findUserRepo(selectedUser) {
    setTimeout(() => {
    console.log('initializing search')
      fetch(`https://api.github.com/users/${selectedUser}/repos`)
      console.log("sent Search")
        .then((response) => response.json())
        .then((data) => {
          console.log("received Search")
          setUserRepo(data);
        });
    }, 4000); // Delay of 6 seconds slow Api
    console.log("completed Search")
  }

  return (
   <BrowserRouter>
     <Routes>
       <Route path='/' element={<Layout setDisplayUserInfo={setDisplayUserInfo} onClickUser={findUserRepo}/>}>
       {displayUserInfo ? (
        <Route  path='' element={<User userRepo={userRepo}/>}/>
      ) : (
        <Route index element={<Home/>}/>
      )}
         <Route  path='contactUs' element={<ContactUs/>}/>
         <Route  path='aboutUs' element={<AboutUs/>}/>
       </Route>
     </Routes>
   </BrowserRouter>
  )
}

export default App
